// Helpers: Bit manipulation
static inline bool IsPowerOfTwo(int v) { return v != 0 && (v & (v - 1)) == 0; }
static inline bool IsPowerOfTwo(U64 v) { return v != 0 && (v & (v - 1)) == 0; }
static inline int UpperPowerOfTwo(int v) {
  v--;
  v |= v >> 1;
  v |= v >> 2;
  v |= v >> 4;
  v |= v >> 8;
  v |= v >> 16;
  v++;
  return v;
}

