// Stacked style modifier, backup of modified data so we can restore it. Data
// type inferred from the variable.
struct StyleMod {
  StyleVar VarIdx;
  union {
    int BackupInt[2];
    float BackupFloat[2];
  };
  StyleMod(StyleVar idx, int v) {
    VarIdx = idx;
    BackupInt[0] = v;
  }
  StyleMod(StyleVar idx, float v) {
    VarIdx = idx;
    BackupFloat[0] = v;
  }
  StyleMod(StyleVar idx, Vec2 v) {
    VarIdx = idx;
    BackupFloat[0] = v.x;
    BackupFloat[1] = v.y;
  }
};

