FontConfig::FontConfig() {
  memset(this, 0, sizeof(*this));
  FontDataOwnedByAtlas = true;
  OversampleH = 2;
  OversampleV = 1;
  GlyphMaxAdvanceX = FLT_MAX;
  RasterizerMultiply = 1.0f;
  RasterizerDensity = 1.0f;
  EllipsisChar = (Wchar)-1;
}

